from paystack._api_resource import APIResource
from typing import TypeVar, cast
from paystack._paystack_object import paystackObject

T = TypeVar("T", bound=paystackObject)


class CreateableAPIResource(APIResource[T]):
    @classmethod
    def create(cls, **params) -> T:
        return cast(
            T,
            cls._static_request("post", cls.class_url(), params=params),
        )
