# -*- coding: utf-8 -*-
"""
The paystack.app_info package is deprecated, please change your
imports to import from paystack directly.
From:
  from paystack.app_info import AppInfo
To:
  from paystack import AppInfo
"""

from typing_extensions import TYPE_CHECKING

# No deprecation warning is raised here, because it would happen
# on every import of `paystack/__init__.py` otherwise. Since that
# module declares its own `app_info` name, this module becomes
# practically impossible to import anyway.

if not TYPE_CHECKING:
    from paystack._app_info import (  # noqa
        AppInfo,
    )
