# -*- coding: utf-8 -*-
from typing_extensions import TYPE_CHECKING
from warnings import warn

warn(
    """
    The paystack.http_client package is deprecated, please change your
    imports to import from paystack directly.
    From:
      from paystack.http_client import HTTPClient
    To:
      from paystack import HTTPClient
    """,
    DeprecationWarning,
    stacklevel=2,
)

if not TYPE_CHECKING:
    from paystack._http_client import *  # noqa
