# -*- coding: utf-8 -*-
from typing_extensions import TYPE_CHECKING
from warnings import warn

warn(
    """
    The paystack.oauth package is deprecated, please change your
    imports to import from paystack directly.
    From:
      from paystack.oauth import OAuth
    To:
      from paystack import OAuth
    """,
    DeprecationWarning,
    stacklevel=2,
)

if not TYPE_CHECKING:
    from paystack._oauth import (  # noqa
        OAuth,
    )
