# Used for global variables
import paystack  # noqa: IMP101
from paystack._error import paystackError


class OAuthError(paystackError):
    def __init__(
        self,
        code,
        description,
        http_body=None,
        http_status=None,
        json_body=None,
        headers=None,
    ):
        super(OAuthError, self).__init__(
            description, http_body, http_status, json_body, headers, code
        )

    def _construct_error_object(self):
        if self.json_body is None:
            return None

        return paystack.error_object.OAuthErrorObject._construct_from(  # pyright: ignore
            values=self.json_body,
            requestor=paystack._APIRequestor._global_instance(),
            api_mode="V1",
        )


class InvalidClientError(OAuthError):
    pass


class InvalidGrantError(OAuthError):
    pass


class InvalidRequestError(OAuthError):
    pass


class InvalidScopeError(OAuthError):
    pass


class UnsupportedGrantTypeError(OAuthError):
    pass


class UnsupportedResponseTypeError(OAuthError):
    pass
