# -*- coding: utf-8 -*-
from typing_extensions import TYPE_CHECKING
from warnings import warn

warn(
    """
    The paystack.paystack_object package is deprecated, please change your
    imports to import from paystack directly.
    From:
      from paystack.paystack_object import paystackObject
    To:
      from paystack import paystackObject
    """,
    DeprecationWarning,
    stacklevel=2,
)

if not TYPE_CHECKING:
    from paystack._paystack_object import (  # noqa
        paystackObject,
    )
