# -*- coding: utf-8 -*-
from typing_extensions import TYPE_CHECKING
from warnings import warn

warn(
    """
    The paystack.webhook package is deprecated, please change your
    imports to import from paystack directly.
    From:
      from paystack.webhook import Webhook
    To:
      from paystack import Webhook
    """,
    DeprecationWarning,
    stacklevel=2,
)

if not TYPE_CHECKING:
    from paystack._webhook import (  # noqa
        Webhook,
        WebhookSignature,
    )
